import React, { useState } from 'react'; // Import useState for managing rotation state
import './About.css'; // Import your CSS for styling

const Card = ({ imgSrc, text }) => {
  const [isRotated, setIsRotated] = useState(false);

  const handleClick = () => {
    setIsRotated(!isRotated);
  };

  return (
    <div className="col-md-3">
      <div
        className={`card mb-4 box-shadow ${isRotated ? 'rotate' : ''}`} // Add 'rotate' class based on state
        style={{ height: '350px' }}
        onClick={handleClick} // Handle click event
      >
        <img className="card-img-top" src={imgSrc} alt="Thumbnail" style={{ height: '225px', width: '100%', objectFit: 'cover' }} />
        <div className="card-body" style={{ height: '125px', overflow: 'hidden' }}>
          <p className="card-text" style={{ overflow: 'auto' }}>{text}</p>
        </div>
      </div>
    </div>
  );
};

